$.templates("pager", "<div>每页" +
		"{{if !choseSelect}}" +
		"{{:PageSize}}" +
		"{{else}}" +
		"<select  autocomplete='off' onchange='$.pagination({{:data}},1);return false;'>" +
		"{{for selectNum}}<option  value='{{> #data}}' {{if #data== ~root.PageSize }}selected {{/if}}> {{> #data}}</option>{{/for}}" +
		"</select>{{/if}}条&nbsp;&nbsp;共{{:PageTotalCount}}条&nbsp;&nbsp;{{if HasPreviousPage}}<a href='javascript:;' onclick='$.pagination({{:data}},1);return false;'>首页</a>&nbsp;&nbsp;<a href='javascript:;' onclick='$.pagination({{:data}},{{:CurrentPage - 1}});return false;'>上一页</a>&nbsp;&nbsp;{{else}}<a style='color:#808080; text-decoration:none' disabled='disabled'>首页</a>&nbsp;&nbsp;<a style='color:#808080; text-decoration:none' disabled='disabled'>上一页</a>&nbsp;&nbsp;{{/if}}&nbsp;{{if HasNextPage}}<a href='javascript:;' onclick='$.pagination({{:data}},{{:CurrentPage + 1}});return false;'>下一页</a>&nbsp;&nbsp;<a href='javascript:;' onclick='$.pagination({{:data}},{{:PageCount}});return false;'>尾页</a>&nbsp;&nbsp;{{else}}<a style='color:#808080; text-decoration:none' disabled='disabled'>下一页</a>&nbsp;&nbsp;<a style='color:#808080; text-decoration:none' disabled='disabled'>尾页</a>&nbsp;&nbsp;{{/if}}{{:CurrentPage}}/{{:PageCount}}&nbsp;&nbsp;<span class='form-inline'> 转到：<input type='text' class='form-control input-small w50' id='_MvcPager_Ctrl0_pib' value='{{:CurrentPage}}' onkeyup = \"value=value.replace(/[^(0-9)]/g,'');\" />&nbsp;&nbsp;<button class='btn btn-default btn-small' onclick='$.pagination({{:data}},$.trim($(this).prev(\"input\").val()));return false;'>确定</button></span></div>");
$.templates("pagerKO", "<div>각각 페이지의{{:PageSize}}조&nbsp;&nbsp;모두{{:PageTotalCount}}조&nbsp;&nbsp;{{if HasPreviousPage}}<a href='javascript:;' onclick='$.pagination({{:data}},1);return false;'>홈</a>&nbsp;&nbsp;<a href='javascript:;' onclick='$.pagination({{:data}},{{:CurrentPage - 1}});return false;'>페이지</a>&nbsp;&nbsp;{{else}}<a style='color:#808080; text-decoration:none' disabled='disabled'>홈</a>&nbsp;&nbsp;<a style='color:#808080; text-decoration:none' disabled='disabled'>페이지</a>&nbsp;&nbsp;{{/if}}&nbsp;{{if HasNextPage}}<a href='javascript:;' onclick='$.pagination({{:data}},{{:CurrentPage + 1}});return false;'>다음 페이지</a>&nbsp;&nbsp;<a href='javascript:;' onclick='$.pagination({{:data}},{{:PageCount}});return false;'>마지막 페이지</a>&nbsp;&nbsp;{{else}}<a style='color:#808080; text-decoration:none' disabled='disabled'>다음 페이지</a>&nbsp;&nbsp;<a style='color:#808080; text-decoration:none' disabled='disabled'>마지막 페이지</a>&nbsp;&nbsp;{{/if}}{{:CurrentPage}}/{{:PageCount}}&nbsp;&nbsp;<span class='form-inline'> 넘어가다：<input type='text' class='form-control input-small w50' id='_MvcPager_Ctrl0_pib' value='{{:CurrentPage}}' onkeyup = \"value=value.replace(/[^(0-9)]/g,'');\" />&nbsp;&nbsp;<button class='btn btn-default btn-small' onclick='$.pagination({{:data}},$.trim($(this).prev(\"input\").val()));return false;'>확인</button></span></div>");